<svelte:component
  this={component}
  {tag}
  bind:this={element}
  use={[forwardEvents, ...use]}
  class={classMap({
    [className]: true,
    [adjustClass]: true,
  })}
  style={Object.entries(internalStyles)
    .map(([name, value]) => `${name}: ${value};`)
    .concat([style])
    .join(' ')}
  {...$$restProps}
>
  <slot />
</svelte:component>

<script>import { get_current_component } from 'svelte/internal';
import { forwardEventsBuilder, classMap } from '@smui/common/internal';
import { SmuiElement } from '@smui/common';
const forwardEvents = forwardEventsBuilder(get_current_component());
// Remember to update $$Props if you add/remove/rename props.
export let use = [];
let className = '';
export { className as class };
export let style = '';
export let bottomAppBar;
let element;
export let component = SmuiElement;
export let tag = (component === SmuiElement ? 'main' : undefined);
let internalStyles = {};
$: propStore = bottomAppBar && bottomAppBar.getPropStore();
$: adjustClass = (() => {
    if (!propStore || $propStore.variant === 'static') {
        return '';
    }
    addStyle('--smui-bottom-app-bar--fab-offset', $propStore.adjustOffset + 'px');
    return `smui-bottom-app-bar--${$propStore.variant}-adjust ${$propStore.withFab ? 'smui-bottom-app-bar--with-fab' : ''}`;
})();
function addStyle(name, value) {
    if (internalStyles[name] != value) {
        if (value === '' || value == null) {
            delete internalStyles[name];
            internalStyles = internalStyles;
        }
        else {
            internalStyles[name] = value;
        }
    }
}
export function getElement() {
    return element.getElement();
}
</script>
