<Paper
  bind:this={element}
  use={usePass}
  class={classMap({
    [className]: true,
    'smui-bottom-app-bar__section': true,
    'smui-bottom-app-bar__section--fab-inset': fabInset,
  })}
  color={$color}
  variant="unelevated"
  square
  {...$$restProps}><slot /></Paper
>

<script>import { getContext } from 'svelte';
import { get_current_component } from 'svelte/internal';
import { forwardEventsBuilder, classMap } from '@smui/common/internal';
import Paper from '@smui/paper';
const forwardEvents = forwardEventsBuilder(get_current_component());
// Remember to update $$Props if you add/remove/rename props.
export let use = [];
$: usePass = [forwardEvents, ...use];
let className = '';
export { className as class };
export let fabInset = false;
let element;
const color = getContext('SMUI:bottom-app-bar:color');
export function getElement() {
    return element.getElement();
}
</script>
