<svelte:component
  this={component}
  {tag}
  bind:this={element}
  use={[forwardEvents, ...use]}
  class={classMap({
    [className]: true,
    'mdc-drawer-scrim': true,
    'smui-drawer-scrim__absolute': !fixed,
  })}
  on:click={handleClick}
  {...$$restProps}
>
  <slot />
</svelte:component>

<script>import { get_current_component } from 'svelte/internal';
import { forwardEventsBuilder, classMap, dispatch, } from '@smui/common/internal';
import { SmuiElement } from '@smui/common';
const forwardEvents = forwardEventsBuilder(get_current_component());
// Remember to update $$Props if you add/remove/rename props.
export let use = [];
let className = '';
export { className as class };
export let fixed = true;
let element;
export let component = SmuiElement;
export let tag = (component === SmuiElement ? 'div' : undefined);
function handleClick(event) {
    dispatch(getElement(), 'SMUIDrawerScrim:click', event);
}
export function getElement() {
    return element.getElement();
}
</script>
